<script setup>
import { onMounted } from 'vue';
import { gsap } from 'gsap';
import ScrollTrigger from 'gsap/ScrollTrigger';

onMounted(() => {
    gsap.registerPlugin(ScrollTrigger)
    let sections = gsap.utils.toArray(".panel");

    let scrollTween = gsap.to(sections, {
        xPercent: -100 * (sections.length - 1),
        ease: "none",
        scrollTrigger: {
            trigger: ".containersd",
            pin: true,
            scrub: 1,
            snap: 1 / (sections.length - 1),
            end: () => "+=" + document.querySelector(".containersd").offsetWidth
        }
    });


    gsap.fromTo('#music_loud', {
        autoAlpha: 0,
        scale: .6

    }, {
        autoAlpha: 1,
        scale: 1,
        ease: 'none',
        duration: .5,
        scrollTrigger: {
            containerAnimation: scrollTween,
            trigger: '.panel',
            start: 'top center',
            // scrub:true,
            // start: '30% 40%',

        }
    })
    gsap.fromTo('#music_slow', {
        autoAlpha: 0,
        scale: .6

    }, {
        autoAlpha: 1,
        scale: 1,
        ease: 'none',
        duration: .5,
        scrollTrigger: {
            containerAnimation: scrollTween,
            trigger: '.panel',
            start: 'top center',
            // scrub:true,
            // start: '30% 40%',

        }
    })
    gsap.fromTo('#music_up', {
        autoAlpha: 0,
        scale: .6

    }, {
        autoAlpha: 1,
        scale: 1,
        ease: 'none',
        duration: .5,
        scrollTrigger: {
            containerAnimation: scrollTween,
            trigger: '.panel',
            start: 'top center',
            // scrub:true,
            // start: '30% 40%',

        }
    })
    gsap.fromTo('#music_low', {
        autoAlpha: 0,
        scale: .6

    }, {
        autoAlpha: 1,
        scale: 1,
        ease: 'none',
        duration: .5,
        scrollTrigger: {
            containerAnimation: scrollTween,
            trigger: '.panel',
            start: 'top center',
            // scrub:true,
            // start: '30% 40%',

        }
    })
    gsap.fromTo('.panel_img_svg', {
        autoAlpha: 0,
        scale: .6

    }, {
        autoAlpha: 1,
        scale: 1,
        ease: 'none',
        duration: .5,
        scrollTrigger: {
            containerAnimation: scrollTween,
            trigger: '.panel_two',
            start: 'top center',
            // markers:true,
            // scrub:true,
            // start: '30% 40%',

        }
    })

    gsap.fromTo('.panel_content_para', {
        y:30

